let phoneMaskEl = document.getElementById('phone-mask');
if (phoneMaskEl) {
  phoneMask() 
}

function phoneMask() {
  const maskOptions = {
      mask: '+{7} (000) 000 00 00'
  };
  const mask = IMask(phoneMaskEl, maskOptions);
}


